package loops;

public class NestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// using nested while loop multi loop in inside of while loop
		int i=0;
		while(i<=4) {
			System.out.println("Hi-bhuvi "+i);
			i++;
		int	j=1;
		while(j<=1) {
			System.out.println("How are you "+i);
		//	i++;
			j++;
		}
		}

	}

}
