package loops;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		// while loop is check condition and print the value
		int i=0;
		
		while(i<=5) {
			System.out.println("Hello while loop");
		
		    i++;
		}
		
		
		int j=4;
		while(j>=0) {
			System.out.println(j);
			j--;
		}
		

	}

}
