package operater;

public class RelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=4;
		int b=7;
		
		// lessThen (<) operator
		System.out.println(a<b);
		
		//greaterThen (>) operator
		System.out.println(a>b);
		
		//lessThenEqual (<=) operator
		System.out.println(a<=b);
		
		//greaterThenEqual (>=) operator
		System.out.println(a>=b);
		
		//EqualEqual (==) operator
		System.out.println(a==b);
		
		//NotEqual (!=) operator
		System.out.println(a!=b);

	}

}
